import React from "react";
import {
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View,
} from "react-native";
import tinycolor from "tinycolor2";
import { COLORPICKER_COLORS, COLORS } from "../constants/colors";
import { LinearGradient } from 'expo-linear-gradient';
import { isValidHex, toState } from "../utils";
import { FONTS } from "../constants/fonts";



function ColorView({ size, color, style }) {
    return (
        typeof color == "object" ?
            <LinearGradient
                colors={[...color]}
                style={[
                    {
                        width: size,
                        height: size,
                        borderRadius: size / 2,
                        backgroundColor: color,
                    },
                    style,
                ]}
            /> : 
        <View
            style={[
                {
                    width: size,
                    height: size,
                    borderRadius: size / 2,
                    backgroundColor: color,
                },
                style,
            ]}
        />

    );
}

export function ColorPicker({
    isMobile,
    onValueChanged,
    ...props
}) {

    const [text, onTextChanged] = React.useState("FFFFFF");
    const COLOR_SIZE = 24;
    const PADDING = 12;
    const SCROLL_HEIGHT = COLOR_SIZE + PADDING * 2;
    const COLORS = React.useMemo(() => COLORPICKER_COLORS.reverse(), []);
    return (
        <ScrollView
            showsVerticalScrollIndicator={false}
            {...props}
            style={[
                isMobile
                    ? { minHeight: SCROLL_HEIGHT, maxHeight: SCROLL_HEIGHT }
                    : { flexShrink: 1, flexGrow: 0, maxWidth: 300, },
                props.style,

            ]}
            contentContainerStyle={[
                { paddingVertical: PADDING },
                !isMobile && {
                    width: "100%",
                    flexDirection: "row",
                    flexWrap: "wrap",
                },
                props.contentContainerStyle,
            ]}
            horizontal={isMobile}
            pagingEnabled
        >
            {COLORS.map((color) => (
                <TouchableOpacity key={color} onPress={() => onValueChanged(color)}>
                    <ColorView
                        size={COLOR_SIZE}
                        color={color}
                        style={{ margin: isMobile ? 12 : 6 }}
                    />
                </TouchableOpacity>
            ))}
            <ColorPickerInput
                size={COLOR_SIZE}
                value={text}
                onValueChanged={onTextChanged}
                onSubmit={(value) => {
                    onValueChanged(value);
                }}
            />

            <TouchableOpacity style={styles.useGradientButton}>
                <Text style={styles.useGradientButtonText}>Use gradients colors</Text>
            </TouchableOpacity>
        </ScrollView>
    );
}

function ColorPickerInput({ size, value, onValueChanged, onSubmit }) {
    return (
        <View
            style={[
                {
                    height: size,
                    flex: 1,
                    minWidth: 275,
                    margin: 10,
                    borderRadius: size / 2,
                    borderWidth: 2,
                    flexDirection: "row",
                    overflow: "hidden",
                },
                {
                    borderColor: "gray",
                    backgroundColor: "gray",
                },
            ]}
        >
            <View
                style={{
                    width: size,
                    height: size - 4,
                    justifyContent: "center",
                    alignItems: "center",
                }}
            >
                <Text
                    style={{ alignSelf: "center", color: "#98A1A4" }}
                >
                    #
                </Text>
            </View>
            <TextInput
                autoCapitalize={"none"}
                autoCorrect={false}
                value={value.replace("#", "")}
                onChangeText={(value) => {
                    onValueChanged(value);

                    if (isValidHex(value)) {
                        onSubmit("#" + tinycolor(value).toHex());
                    }
                }}
                style={{
                    flex: 1,
                    paddingHorizontal: 4,
                    color: "#fff",
                    outlineColor: "transparent",
                }}
            />
        </View>
    );
}




const styles = StyleSheet.create({
    useGradientButton: {
        marginVertical: 10,
        width: "100%"
    },
    useGradientButtonText: {
        textAlign: 'center',
        fontFamily: FONTS.medium,
    }
})
